import React, { useState, Component } from 'react';
import { Alert, Pressable, View, Text, StyleSheet, Button, TouchableOpacity, ScrollView } from 'react-native';
import Modal from 'react-native-modal';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingVertical: 20,
    paddingHorizontal: 5,
  },

  view1: {
    flexDirection: 'row',
    flex: 1,
    flexWrap: 'wrap',
    borderWidth: 1,
    borderColor: "#cecdcd",
    paddingVertical: 20,
    // alignItems: "center",
    justifyContent: "center",
  },

  button: {
    marginHorizontal: 5,
    marginVertical: 5,
    borderRadius: 100,
    borderColor: "black",
    width: 90,
    height: 90,
    // alignItems: "center",
    justifyContent: "center"
  },

  color1: {
    backgroundColor: "#008080"
  },

  color2: {
    backgroundColor: "#dc143c"
  },

  color3: {
    backgroundColor: "#ee6aa7"
  },

  color4: {
    backgroundColor: "#0080ff"
  },

  color5: {
    backgroundColor: "#a327db"
  },

  buttonText: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },

  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button1: {
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }

});

export default class HomeScreen extends Component {
  constructor(props) {
    super(props);
  }
  // state = {
  //   modalVisible: false
  // };

  // setModalVisible = (visible) => {
  //   this.setState({ modalVisible: visible });
  // }
  render() {
    // const { modalVisible } = this.state;
    return (
      <View style={styles.container}>
        {/* <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => {
            Alert.alert("Modal has been closed.");
            this.setModalVisible(!modalVisible);
          }}
        >
          <View style={styles.centeredView}>
            <View style={styles.modalView}>
              <Text style={styles.modalText}>Hello World!</Text>
              <Pressable
                style={[styles.button1, styles.buttonClose]}
                onPress={() => this.setModalVisible(!modalVisible)}
              >
                <Text style={styles.textStyle}>Hide Modal</Text>
              </Pressable>
            </View>
          </View>
        </Modal>
        <Pressable
          style={[styles.button1, styles.buttonOpen]}
          onPress={() => this.setModalVisible(true)}
        >
          <Text style={styles.textStyle}>Show Modal</Text>
        </Pressable> */}


        <ScrollView>
          <View style={styles.view1}>
            <TouchableOpacity
              onPress={() => this.props.navigation.navigate('Venue')}
              style={[styles.button, styles.color1]}
            >
              <View>
                <Text style={styles.buttonText}>Venue</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity
              onPress={() => this.props.navigation.navigate('BridalDress')}
              style={[styles.button, styles.color2]}
            >
              <View>
                <Text style={styles.buttonText}>Bridal</Text>
                <Text style={styles.buttonText}>Dress</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity
              onPress={() => this.props.navigation.navigate('Vendors')}
              style={[styles.button, styles.color3]}
            >
              <View>
                <Text style={styles.buttonText}>+3</Text>
                <Text style={styles.buttonText}>more</Text>
              </View>
            </TouchableOpacity>
          </View>
        </ScrollView>
      </View>
    )
  }
}
