import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { StyleSheet, Text, View, TouchableOpacity, ScrollView, Image, TextInput } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const ViewBridalDress = props => (
    <View style={styles.tab}>
        <Text style={styles.name}>{props.var.name}</Text>
        <Text>{props.var.address}</Text>
        <Text>{props.var.number}</Text>
        <Text style={styles.price}>{props.var.minPrice}</Text>
    </View>
)


export default class BrideDressList extends Component {
    constructor(props) {
        super(props);

        this.state = { bridaldresses: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:3000/api/bridalDressModel')
            .then(response => {
                this.setState({ bridaldresses: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    bridalDressList() {
        return this.state.bridaldresses.map(currentbridaldress => {
            return <ViewBridalDress var={currentbridaldress} key={currentbridaldress._id} />;
        })
    }

    render() {
        return (
            <View style={styles.container}>
                <ScrollView>
                    <View style={styles.searchView}>
                        <TextInput
                            placeholder="Search Vendors..."
                            style={styles.search}
                        />
                        <TouchableOpacity style={styles.searchButton}>
                            <Text style={styles.searchText}>
                                <Ionicons name="search-outline" size={20} color="white" />
                            </Text>
                        </TouchableOpacity>
                    </View>

                    <View >
                        {this.bridalDressList()}
                    </View>
                </ScrollView>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        marginHorizontal: 20,
        marginTop: 40,
        marginBottom: 10,
    },

    tab: {
        alignItems: "left",
        width: "100%",
        height: 150,
        marginTop: 30,
        justifyContent: "center",
        borderColor: "black",
    },

    name: {
        fontSize: 30,
    },

    price: {
        fontSize: 30,
        fontWeight: "bold",
    },

    searchView: {
        flexDirection: "row",
        justifyContent: "space-evenly",
        alignItems: "center",
    },

    search: {
        height: 40,
        width: "80%",
        borderColor: "transparent",
        borderWidth: 1,
        paddingHorizontal: 10,
        borderRadius: 20,
        backgroundColor: "#dddddd",
    },

    searchButton: {
        alignItems: "center",
        backgroundColor: "#980007",
        // backgroundColor: "#e32016",
        width: "15%",
        height: 40,
        justifyContent: "center",
    },

    searchText: {
        color: "white",
        fontSize: 20,
        fontWeight: 'bold',
    },
});
