import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { StyleSheet, Text, View, TouchableOpacity, ScrollView, Image, TextInput } from 'react-native';

const styles = StyleSheet.create({
    container: {
        marginHorizontal: 20,
        marginTop: 40,
        marginBottom: 10,
    },

    tab: {
        alignItems: "left",
        width: "100%",
        height: 150,
        marginTop: 30,
        justifyContent: "center",
    },
});

const ViewBridalDress = props => (
    <View>
        <Text>{props.var.name}</Text>
        <Text>{props.var.number}</Text>
        <Text>{props.var.address}</Text>
        <Text>{props.var.minPrice}</Text>
    </View>
  )
  

export default class BrideDressList extends Component {
    constructor(props) {
      super(props);
  
      this.state = {bridaldresses: []};
    }
  
    componentDidMount() {
      axios.get('http://localhost:3000/api/bridalDressModel')
        .then(response => {
          this.setState({ bridaldresses: response.data })
        })
        .catch((error) => {
          console.log(error);
        })
    }
  
    bridalDressList() {
      return this.state.bridaldresses.map(currentbridaldress => {
        return <ViewBridalDress var={currentbridaldress} key={currentbridaldress._id}/>;
      })
    }
  
    render() {
      return (
          <View style={styles.container}>
              <ScrollView>
                  <View style={styles.tab}>
                    { this.bridalDressList() }
                  </View>
              </ScrollView>
          </View>
      )
    }
  }

