import React, { Component } from 'react';
import axios from 'axios';
import { StyleSheet, Text, View, TouchableOpacity, ScrollView, Image, TextInput } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';

const ViewVenue = props => (
    <TouchableOpacity style={styles.button}>
        <View>
            <Text style={styles.name}>{props.var.name}</Text>
            <Text>{props.var.address}</Text>
            <Text>Capacity: {props.var.noOfPeople} people</Text>
            <Text>{"\n"}</Text>
            <Text style={styles.number}>{props.var.number}</Text>
            <Text>{"\n"}</Text>
            <Text>Starting from:</Text>
            <Text style={styles.price}>Rs. {props.var.minPrice}</Text>

            <TouchableOpacity>
                        <Entypo name="star" size={24} color="black" />                           
                        </TouchableOpacity>
        </View>
    </TouchableOpacity>
)

export default class VenueList extends Component {
    constructor(props) {
        super(props);

        this.state = { venues: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:3000/api/venueModel')
            .then(response => {
                this.setState({ venues: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    venueList() {
        return this.state.venues.map(currentvenue => {
            return <ViewVenue var={currentvenue} key={currentvenue._id} />;
        })
    }

    render() {
        return (
            <View style={styles.container}>
                <ScrollView>
                    <View style={styles.searchView}>
                        <TextInput
                            placeholder="Search Venues..."
                            style={styles.search}
                        />
                        <TouchableOpacity style={styles.searchButton}>
                            <Text style={styles.searchText}>
                                <Ionicons name="search-outline" size={20} color="white" />
                            </Text>
                        </TouchableOpacity>
                    </View>

                    <View >
                        {this.venueList()}
                    </View>
                </ScrollView>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        marginHorizontal: 20,
        marginTop: 40,
        marginBottom: 10,
    },

    button: {
        // alignItems: "left",
        width: "100%",
        height: 250,
        marginTop: 30,
        justifyContent: "center",
        borderWidth: 1,
        borderColor: "black",
        paddingHorizontal: 20,
        paddingVertical: 20,
    },

    name: {
        fontSize: 30,
    },

    number: {
        fontSize: 20,
    },

    price: {
        fontSize: 30,
        fontWeight: "bold",
    },

    searchView: {
        flexDirection: "row",
        justifyContent: "space-evenly",
        alignItems: "center",
    },

    search: {
        height: 40,
        width: "80%",
        borderColor: "transparent",
        borderWidth: 1,
        paddingHorizontal: 10,
        borderRadius: 20,
        backgroundColor: "#dddddd",
    },

    searchButton: {
        alignItems: "center",
        backgroundColor: "#980007",
        // backgroundColor: "#e32016",
        width: "15%",
        height: 40,
        justifyContent: "center",
    },

    searchText: {
        color: "white",
        fontSize: 20,
        fontWeight: 'bold',
    },
});
