import React, { useState } from 'react';
import { Alert, Pressable, View, Text, StyleSheet, Button, TouchableOpacity, ScrollView } from 'react-native';
import Modal from 'react-native-modal';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingVertical: 20,
    paddingHorizontal: 5,
  },

  view1: {
    flexDirection: 'row',
    flex: 1,
    flexWrap: 'wrap',
    borderWidth: 1,
    borderColor: "#cecdcd",
    paddingVertical: 20,
    // alignItems: "center",
    justifyContent: "center",
  },

  button: {
    marginHorizontal: 5,
    marginVertical: 5,
    borderRadius: 100,
    borderColor: "black",
    width: 90,
    height: 90,
    // alignItems: "center",
    justifyContent: "center"
  },

  color1: {
    backgroundColor: "#008080"
  },

  color2: {
    backgroundColor: "#dc143c"
  },

  color3: {
    backgroundColor: "#ee6aa7"
  },

  color4: {
    backgroundColor: "#0080ff"
  },

  color5: {
    backgroundColor: "#a327db"
  },

  buttonText: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },

  heading: {
    fontSize: 20,
    fontWeight: "bold",
    textAlign: "left",
  },

});

function HomeScreen() {
  const [isModalVisible, setModalVisible] = useState(false);
  
  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

    return (
      <View style={{flex: 1}}>
        <Button title="Show modal" onPress={toggleModal} />

        <Modal isVisible={isModalVisible}>
          <View style={{flex: 1}}>
            <Text>Hello!</Text>

            <Button title="Hide modal" onPress={toggleModal} />
          </View>
        </Modal>
        {HomeScreen1}
      </View>
    );
}

const HomeScreen1 = ({ navigation }) => {
  return (
    <View style={styles.container}>
      {this.ModalTester}
      <ScrollView>
          <Text style={styles.heading}>Vendors</Text>
          <Text>{"\n"}</Text>
        <View style={styles.view1}>
          <TouchableOpacity
            onPress={() => navigation.navigate('Venue')}
            style={[styles.button, styles.color1]}
          >
            <View>
              <Text style={styles.buttonText}>Venue</Text>
            </View>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={() => navigation.navigate('BridalDress')}
            style={[styles.button, styles.color2]}
          >
            <View>
              <Text style={styles.buttonText}>Bridal</Text>
              <Text style={styles.buttonText}>Dress</Text>
            </View>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={() => navigation.navigate('Caterer')}
            style={[styles.button, styles.color3]}
          >
            <View>
              <Text style={styles.buttonText}>Caterer</Text>
            </View>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={() => navigation.navigate('Makeup')}
            style={[styles.button, styles.color4]}
          >
            <View>
              <Text style={styles.buttonText}>Makeup</Text>
            </View>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={() => navigation.navigate('Photographer')}
            style={[styles.button, styles.color5]}
          >
            <View>
              <Text style={styles.buttonText}>Photographer</Text>
            </View>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </View>
  );
};

export default HomeScreen;

