import React, { Component } from 'react';
import { useState } from 'react';
import axios from 'axios';
import { StyleSheet, Text, View, TouchableOpacity, ScrollView, Image, TextInput, Modal } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

// const ViewGuestList = props => (
//     <TouchableOpacity style={styles.button}>
//         <View>
//             <Text style={styles.name}>{props.var.name}</Text>
//             <Text>{props.var.address}</Text>
//             <Text>{props.var.foodType}</Text>
//             <Text>{"\n"}</Text>
//             <Text style={styles.number}>{props.var.number}</Text>
//             <Text>{"\n"}</Text>
//             <Text>Starting from:</Text>
//             <Text style={styles.price}>Rs. {props.var.minPrice}</Text>
//         </View>
//     </TouchableOpacity>
// )

export default class GuestListList extends Component {
    constructor(props) {
        super(props);

        this.state = { caterers: [] };
    }

    // componentDidMount() {
    //     axios.get('http://localhost:3000/api/catererModel')
    //         .then(response => {
    //             this.setState({ caterers: response.data })
    //         })
    //         .catch((error) => {
    //             console.log(error);
    //         })
    // }

    // catererList() {
    //     return this.state.caterers.map(currentcaterer => {
    //         return <ViewCaterer var={currentcaterer} key={currentcaterer._id} />;
    //     })
    // }

        showModal() {
            const [modalVisible, setModalVisible] = useState(false);
            setModalVisible(true);
            <Modal
            animationType="slide"
            transparent={true}
            visible={modalVisible}
            // onRequestClose={() => {Alert.alert('Modal has been closed.');}}
        >
            <Text style={styles.modalTitle}>Enter Guest Information:</Text>
            <Text>Guest name:</Text>
            <TextInput/>
            <Text>Number:</Text>
            <TextInput/>
            <Text>Address:</Text>
            <TextInput/>
            {/* <Button onPress={() => }>Save</Button> */}
        </Modal>
    }

    render() {
        return (
            <View style={styles.container}>
                <ScrollView>
                    <View style={styles.searchView}>
                        <TextInput
                            placeholder="Search Guests..."
                            style={styles.search}
                        />
                        <TouchableOpacity style={styles.searchButton}>
                            <Text style={styles.searchText}>
                                <Ionicons name="search-outline" size={20} color="white" />
                            </Text>
                        </TouchableOpacity>
                    </View>

                    <View >
                        <TouchableOpacity onPress={this.showModal()}>
                            <View>
                                <Text>Add new guest</Text>
                            </View>
                        </TouchableOpacity>

                        {/* {this.catererList()} */}
                    </View>
                </ScrollView>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        marginHorizontal: 20,
        marginTop: 40,
        marginBottom: 10,
    },

    button: {
        alignItems: "left",
        width: "100%",
        height: 200,
        marginTop: 30,
        justifyContent: "center",
        borderWidth: 1,
        borderColor: "black",
        paddingHorizontal: 20,
        paddingVertical: 20,
    },

    modalTitle: {
        fontSize: 30,
        fontWeight: "bold",
    },

    searchView: {
        flexDirection: "row",
        justifyContent: "space-evenly",
        alignItems: "center",
    },

    search: {
        height: 40,
        width: "80%",
        borderColor: "transparent",
        borderWidth: 1,
        paddingHorizontal: 10,
        borderRadius: 20,
        backgroundColor: "#dddddd",
    },

    searchButton: {
        alignItems: "center",
        backgroundColor: "#980007",
        // backgroundColor: "#e32016",
        width: "15%",
        height: 40,
        justifyContent: "center",
    },

    searchText: {
        color: "white",
        fontSize: 20,
        fontWeight: 'bold',
    },
});
